// Get the chat input
const chatInput = $('Chat').first().json.chatInput;

// Extract text between quotes using regex
const match = chatInput.match(/"([^"]*)"/);

// Get the extracted text or use original if no quotes found
const extractedText = match ? match[1] : chatInput;

return {
  json: {
    text: extractedText,
    originalInput: chatInput
  }
};
